function ttsengine_get_iOS6PostText(id){var text='';for(var i=0;i<vars.tts_posts.length;i++){if(parseInt(vars.tts_posts[i].id)===parseInt(id)){text=vars.tts_posts[i].title+"\r\n"+vars.tts_posts[i].content;text=ttsengine_html_strip(text);text=encodeURIComponent(text);text=text.replace(/%0A/g,'.');text=text.replace(/%0D%0A/g,'.');}}
return text;}
function ttsengine_html_strip(html){var tmp=document.createElement("DIV");tmp.innerHTML=html;return tmp.textContent||tmp.innerText;}
function ttsengine_processTTS(listenButton){ttsengine_activeButton=listenButton;var id=listenButton.id;if(ttsengine_inProcess)ttsengine_haltProcess();ttsengine_inProcess=true;ttsengine_currentPostId=id.replace('listenbutton','');jQuery("#"+id).attr('onclick','').unbind('click');listenButton.onclick=function(){ttsengine_haltProcess();};var imagecontainer=document.getElementById("tts-imagecontainer"+ttsengine_currentPostId);imagecontainer.style.visibility="visible";if(ttsengine_isMobileSafari){if(ttsengine_iOSVersion===6){ttsengine_modifyButtonText(vars.loading_phrase);var text=ttsengine_get_iOS6PostText(ttsengine_currentPostId);ttsengine_ajaxGetiOS6RequestURL(text);}
else{ttsengine_modifyButtonText(vars.stop_phrase);ttsengine_playMobileSafariUrl(ttsengine_currentPostId);}}
else{ttsengine_modifyButtonText(vars.stop_phrase);var text=ttsengine_getPostText(ttsengine_currentPostId);ttsengine_ajaxGetSecureRequestURLs(text);}}
function ttsengine_load_iOS6Audio(url){var request=new XMLHttpRequest();request.open('GET',url,true);request.responseType='arraybuffer';request.onload=function(){ttsengine_context.decodeAudioData(request.response,function(buffer){ttsengine_create_iOS6AudioSource(buffer);},ttsengine_onLoadError);}
request.send();}
function ttsengine_create_iOS6AudioSource(buffer){ttsengine_currentSource=ttsengine_context.createBufferSource();ttsengine_currentSource.buffer=buffer;var duration=buffer.duration;ttsengine_currentSource.connect(ttsengine_context.destination);ttsengine_resetImageContainer();jQuery("#"+ttsengine_activeButton.id).attr('onclick','').unbind('click');ttsengine_modifyButtonText(vars.play_phrase);ttsengine_activeButton.onclick=function(){ttsengine_play_iOS6Audio(duration);};}
function ttsengine_play_iOS6Audio(duration){jQuery("#"+ttsengine_activeButton.id).attr('onclick','').unbind('click');ttsengine_currentSource.noteOn(0);ttsengine_timeout=setTimeout(function(){ttsengine_haltProcess();},duration*1000);ttsengine_modifyButtonText(vars.stop_phrase);ttsengine_activeButton.onclick=function(){ttsengine_haltProcess();};}
function ttsengine_onLoadError(){alert("Error: Failed to load Audio");}
function ttsengine_playMobileSafariUrl(postID){ttsengine_sectionURLs=ttsengine_iOS_URLs[postID];ttsengine_currentSectionID=0;ttsengine_audioPlayer.src=ttsengine_sectionURLs[0];ttsengine_audioPlayer.load();ttsengine_audioPlayer.play();}
function ttsengine_mobileSafariLoadstart(){ttsengine_interval=setInterval(function(){ttsengine_checkForPlaybackEnd();},100);}
function ttsengine_checkForPlaybackEnd(){if(isFinite(ttsengine_audioPlayer.duration)){clearInterval(ttsengine_interval);ttsengine_currentSectionID++;if(ttsengine_currentSectionID<ttsengine_sectionURLs.length){ttsengine_audioPlayer.src=ttsengine_sectionURLs[ttsengine_currentSectionID];ttsengine_audioPlayer.load();ttsengine_audioPlayer.play();}
else{ttsengine_resetListenButton();ttsengine_inProcess=false;}}}
function ttsengine_onMobileSafariPlaying(){ttsengine_resetImageContainer();}
function ttsengine_getPostSections(text){var str='';var tmp=text.split('%0D%0A');var minLength=100;var sections=[];var match;var buffer='';for(var i=0;i<tmp.length;i++){match=tmp[i].match(/[a-zA-Z]/g);if(match){buffer=tmp[i];while(buffer.length<minLength&&(i<(tmp.length-1))){i++;match=tmp[i].match(/[a-zA-Z]/g);if(match)buffer+=tmp[i];}
str=str+buffer+' ';}}
return str;}
function ttsengine_getPostText(id){var text='';for(var i=0;i<vars.tts_posts.length;i++){if(parseInt(vars.tts_posts[i].id)===parseInt(id)){text=vars.tts_posts[i].title+"\r\n"+vars.tts_posts[i].content;text=ttsengine_html_strip(text);text=encodeURIComponent(text);text=text.replace(/%0D%0A/g,'.%0D%0A');text=text.replace(/%0A/g,'.%0D%0A');}}
return text;}
function ttsengine_haltProcess(){if(ttsengine_currentSource)ttsengine_currentSource.noteOff(0);if(ttsengine_timeout)clearTimeout(ttsengine_timeout);if(ttsengine_interval)clearInterval(ttsengine_interval);ttsengine_resetListenButton();ttsengine_resetImageContainer();if(ttsengine_isMobileSafari&&(ttsengine_iOSVersion!==6))ttsengine_audioPlayer.pause();if(ttsengine_jPlayer_1)ttsengine_jPlayer_1.clearMedia();if(ttsengine_jPlayer_2)ttsengine_jPlayer_2.clearMedia();ttsengine_inProcess=false;}
function ttsengine_ajaxRequestConnection(){var ajaxRequest;try{ajaxRequest=new XMLHttpRequest();}catch(e){try{ajaxRequest=new ActiveXObject("Msxml2.XMLHTTP");}catch(e){try{ajaxRequest=new ActiveXObject("Microsoft.XMLHTTP");}catch(e){alert("Ajax Connection Error");return false;}}}
return ajaxRequest;}
function ttsengine_ajaxGetSafariRequestURLs(ajaxParams){var ajaxConn=ttsengine_ajaxRequestConnection();if(ajaxConn===null)return;ajaxConn.open("POST",vars.ajax_safari,true);ajaxConn.setRequestHeader("Content-type","application/x-www-form-urlencoded");ajaxConn.onreadystatechange=function(){if(ajaxConn.readyState==4){var response=JSON.parse(ajaxConn.responseText);if(response.error){alert(response.error);}
else{ttsengine_iOS_URLs=response.iOS_URLs;}}};ajaxConn.send(ajaxParams);}
function ttsengine_ajaxGetiOS6RequestURL(text){var ajaxConn=ttsengine_ajaxRequestConnection();if(ajaxConn===null)return;var ajaxParams="text="+text+"&language="+vars.language+"&siteurl="+vars.site_url+"&linkenabled="+vars.link_enabled+"&abspath="+vars.abs_path;ajaxConn.open("POST",vars.ajax_iOS6,true);ajaxConn.setRequestHeader("Content-type","application/x-www-form-urlencoded");ajaxConn.onreadystatechange=function(){if(ajaxConn.readyState==4){var response=JSON.parse(ajaxConn.responseText);if(response.error){alert(response.error);}
else{ttsengine_iOS6URL=response.iOS6URL;ttsengine_load_iOS6Audio(ttsengine_iOS6URL);}}};ajaxConn.send(ajaxParams);}
function ttsengine_ajaxGetSecureRequestURLs(text){var ajaxConn=ttsengine_ajaxRequestConnection();if(ajaxConn===null)return;var ajaxParams="text="+text+"&language="+vars.language+"&siteurl="+vars.site_url+"&linkenabled="+vars.link_enabled+"&abspath="+vars.abs_path;ajaxConn.open("POST",vars.ajax,true);ajaxConn.setRequestHeader("Content-type","application/x-www-form-urlencoded");ajaxConn.onreadystatechange=function(){if(ajaxConn.readyState==4){var response=JSON.parse(ajaxConn.responseText);if(response.error){alert(response.error);ttsengine_haltProcess();}
else{ttsengine_sectionURLs=response.secureRequestURLs;ttsengine_playTTS();}}};ajaxConn.send(ajaxParams);}
function ttsengine_playTTS(){ttsengine_currentSectionID=0;var firstUrl=ttsengine_sectionURLs[0];var secondUrl=((ttsengine_sectionURLs.length>1)?ttsengine_sectionURLs[1]:'');if(ttsengine_jPlayersInitialized){ttsengine_jPlayer_1.setMedia({mp3:firstUrl,wav:firstUrl,oga:firstUrl}).play();if(ttsengine_sectionURLs.length>1)
ttsengine_jPlayer_2.setMedia({mp3:secondUrl,wav:secondUrl,oga:secondUrl});}
else
ttsengine_initializeJPlayers(firstUrl,secondUrl);}
function ttsengine_initializeJPlayers(firstUrl,secondUrl){ttsengine_addErrorEvent("#jquery_jplayer_1");ttsengine_addErrorEvent("#jquery_jplayer_2");ttsengine_addLoadStartEvent("#jquery_jplayer_1");ttsengine_jPlayer_1=new ttsengine_JPlayer("#jquery_jplayer_1",{mp3:firstUrl,wav:firstUrl,oga:firstUrl},ttsengine_jPlayerOptions);ttsengine_jPlayer_2=new ttsengine_JPlayer("#jquery_jplayer_2",{mp3:secondUrl,wav:secondUrl,oga:secondUrl},ttsengine_jPlayerOptions);ttsengine_jPlayersInitialized=true;}
function ttsengine_addErrorEvent(jPlayerID){jQuery(jPlayerID).bind(jQuery.jPlayer.event.error,function(event){ttsengine_onError(event);});}
function ttsengine_onError(event){ttsengine_resetListenButton();ttsengine_resetImageContainer();switch(event.jPlayer.error.type){case jQuery.jPlayer.error.URL:alert("Error: Network Connection error.");break;case jQuery.jPlayer.error.NO_SOLUTION:alert("Error: Your browser does not support the audio format");break;case jQuery.jPlayer.error.NO_SUPPORT:alert("Error: Your browser does not support the audio format");break;case jQuery.jPlayer.error.URL_NOT_SET:alert("Error: Network Connection error.");break;case jQuery.jPlayer.error.VERSION:alert("Error: Browser compatibility error.");break;case jQuery.jPlayer.error.FLASH_DISABLED:alert("Error: Flash is disabled in your browser.");break;}}
function ttsengine_addLoadStartEvent(jPlayerID){jQuery(jPlayerID).bind(jQuery.jPlayer.event.loadstart,function(event){ttsengine_interval=setInterval(function(){ttsengine_onPlayBack(jPlayerID);},100);});}
function ttsengine_onPlayBack(jPlayerID){var playTime=jQuery(jPlayerID).data().jPlayer.status.currentTime;if(playTime>0){if(ttsengine_currentSectionID==0)
ttsengine_resetImageContainer();clearInterval(ttsengine_interval);}}
function ttsengine_onPlayBackEnd(jPlayerID){if(ttsengine_currentSectionID==(ttsengine_sectionURLs.length-1)){ttsengine_resetListenButton();ttsengine_inProcess=false;ttsengine_currentSectionID==0;}
else{ttsengine_currentSectionID++;ttsengine_playNextSection();if(ttsengine_currentSectionID<(ttsengine_sectionURLs.length-1)){if(jPlayerID=='#jquery_jplayer_1')
ttsengine_jPlayer_1.setMedia({mp3:ttsengine_sectionURLs[ttsengine_currentSectionID+1],wav:ttsengine_sectionURLs[ttsengine_currentSectionID+1],oga:ttsengine_sectionURLs[ttsengine_currentSectionID+1]});else
ttsengine_jPlayer_2.setMedia({mp3:ttsengine_sectionURLs[ttsengine_currentSectionID+1],wav:ttsengine_sectionURLs[ttsengine_currentSectionID+1],oga:ttsengine_sectionURLs[ttsengine_currentSectionID+1]});}}}
function ttsengine_playNextSection(){if((ttsengine_currentSectionID%2)==0)
ttsengine_jPlayer_1.play();else
ttsengine_jPlayer_2.play();}
function ttsengine_resetListenButton(){jQuery("#listenbutton"+ttsengine_currentPostId).attr('onclick','').unbind('click');var button=document.getElementById("listenbutton"+ttsengine_currentPostId);button.onclick=function(){ttsengine_processTTS(this);};ttsengine_modifyButtonText(vars.listen_phrase);}
function ttsengine_resetImageContainer(){var imageContainer=document.getElementById("tts-imagecontainer"+ttsengine_currentPostId);imageContainer.style.visibility="hidden";}
function ttsengine_modifyButtonText(buttonText){var button=document.getElementById("listenbutton"+ttsengine_currentPostId);button.innerHTML='<em></em>'+buttonText;}
